properties.property:

"query": {
       "bool": {
           "filter": [
               ## 可以设置同步数据的过滤参数条件
               #*
               {
                   "term": {
                       "var1.keyword": #[v1]
                   }
               },
               {
                   "term": {
                       "aaaa": #[aaaa]
                   }
               },
               {
                   "term": {
                       "var3.keyword": #[var3]
                   }
               },
               *#
               {   ## 增量检索范围，可以是时间范围，也可以是数字范围，这里采用的是数字增量字段
                   "range": {

                       "time": {
                           "gt": #[time],
                           "lte": #[time__endTime]
                       }


                   }
               }
           ]
       }
   }


 {
    "size":#[size],
    "query": {
        "bool": {
            "filter": [

                {   ## 增量检索范围，可以是时间范围，也可以是数字范围，这里采用的是数字增量字段
                    "range": {

                        "@timestamp": {
                            "gt": #[@timestamp],
                            "lte": #[@timestamp__endTime]
                        }


                    }
                }
            ]
        }
    }
}


 {
    "size":#[size],
    "query": {
        "bool": {
            "filter": [

                {   ## 增量检索范围，可以是时间范围，也可以是数字范围，这里采用的是数字增量字段
                    "range": {

                        "@timestamp": {
                            "gt": #[@timestamp]
                        }


                    }
                }
            ]
        }
    }
}


 {
   "slice": {
        "id": #[sliceId], ## 必须使用sliceId作为变量名称
        "max": #[sliceMax] ## 必须使用sliceMax作为变量名称
    },
    "size":#[size],
    @{queryCondition}
}
